import React from "react";
import m from "./Contact.module.scss";
import emailjs from "emailjs-com";

function sendEmail(e) {
  e.preventDefault();

  emailjs
    .sendForm(
      "service_g98udmq",
      "template_gtzyf7b",
      e.target,
      "user_Svb5pgKvX7J029wvT9Jvs"
    )
    .then(
      (result) => {
        console.log(result.text);
      },
      (error) => {
        console.log(error.text);
      }
    );
  e.target.reset();
}

const Contact = () => {
  return (
    <div className={m.loginbox}>
      <form className={m.contactform} onSubmit={sendEmail}>
        <h2>Contact</h2>
        <div className={m.userbox}>
          <label>Name</label>
          <input type="text" name="name" />
        </div>
        <div className={m.userbox}>
          <label>Email</label>
          <input type="text" name="email" required="" />
        </div>
        <div className={m.user - box}>
          <label>Contact-Number</label>
          <input type="contact_number" name="contact_number" required="" />
        </div>
        <div className={m.user - box}>
          <label>Message</label>
          <input type="message" name="message" required="" />
        </div>
        <button className={m.btn}>SUBMIT</button>
      </form>
    </div>
  );
};

export default Contact;
