import m from "./Contact.module.scss";

import emailjs from "emailjs-com";

function sendEmail(e) {
  e.preventDefault();

  emailjs
    .sendForm(
      "service_g98udmq",
      "template_gtzyf7b",
      e.target,
      "user_Svb5pgKvX7J029wvT9Jvs"
    )
    .then(
      (result) => {
        console.log(result.text);
      },
      (error) => {
        console.log(error.text);
      }
    );
  e.target.reset();
}

