import React from "react";
import "./Contact.css";
import emailjs from "emailjs-com";

// import GoogleMap from "./GoogleMap/Map";

function sendEmail(e) {
  e.preventDefault();

  emailjs
    .sendForm(
      "service_g98udmq",
      "template_gtzyf7b",
      e.target,
      "user_Svb5pgKvX7J029wvT9Jvs"
    )
    .then(
      (result) => {
        console.log(result.text);
      },
      (error) => {
        console.log(error.text);
      }
    );
  e.target.reset();
}
const Contact = () => {
  return (
    <div class="login-box">
      <form className="contact-form" onSubmit={sendEmail}>
        <h2>Contact</h2>
        <div class="user-box">
          <label>Name</label>
          <input type="text" name="name" />
        </div>
        <div class="user-box">
          <label>Email</label>
          <input type="text" name="email" required="" />
        </div>
        <div class="user-box">
          <label>Contact-Number</label>
          <input type="contact_number" name="contact_number" required="" />
        </div>
        <div class="user-box">
          <label>Message</label>
          <input type="message" name="message" required="" />
        </div>
        <button class="btn">SUBMIT</button>
      </form>
    </div>
  );
};
export default Contact;

//import * as React from 'react';
//import * as ReactDOM from 'react-dom';

const { FormWithConstraints, FieldFeedbacks, FieldFeedback } =
  ReactFormWithConstraints;

class Form extends React.Component {
  handleChange = (e) => {
    this.form.validateFields(e.target);
  };

  contactSubmit = (e) => {
    e.preventDefault();

    this.form.validateFields();

    if (!this.form.isValid()) {
      console.log("form is invalid: do not submit");
    } else {
      console.log("form is valid: submit");
    }
  };

  render() {
    return (
      <FormWithConstraints
        ref={(form) => (this.form = form)}
        onSubmit={this.contactSubmit}
        noValidate
      >
        <div className="col-md-6">
          <input
            name="name"
            size="30"
            placeholder="Name"
            required
            onChange={this.handleChange}
            className="form-control"
          />
          <FieldFeedbacks for="name">
            <FieldFeedback when="*" />
          </FieldFeedbacks>

          <input
            type="email"
            name="email"
            size="30"
            placeholder="Email"
            required
            onChange={this.handleChange}
            className="form-control"
          />
          <FieldFeedbacks for="email">
            <FieldFeedback when="*" />
          </FieldFeedbacks>

          <input
            name="phone"
            size="30"
            placeholder="Phone"
            required
            onChange={this.handleChange}
            className="form-control"
          />
          <FieldFeedbacks for="phone">
            <FieldFeedback when="*" />
          </FieldFeedbacks>

          <input
            name="address"
            size="30"
            placeholder="Address"
            required
            onChange={this.handleChange}
            className="form-control"
          />
          <FieldFeedbacks for="address">
            <FieldFeedback when="*" />
          </FieldFeedbacks>
        </div>

        <div className="col-md-6">
          <textarea
            name="comments"
            cols="40"
            rows="20"
            placeholder="Message"
            required
            minLength={5}
            maxLength={50}
            onChange={this.handleChange}
            className="form-control"
          />
          <FieldFeedbacks for="comments">
            <FieldFeedback when="*" />
          </FieldFeedbacks>
        </div>

        <div className="col-md-12">
          <button className="btn btn-lg btn-primary">Send Message</button>
        </div>
      </FormWithConstraints>
    );
  }
}

const App = () => (
  <div className="container">
    <br />
    <Form />
  </div>
);

ReactDOM.render(<App />, document.getElementById("app"));
